import React from 'react';
import "./previewstyle.css"
import { ButtonClicked,updateclick } from './players';
import {createContext} from "react"



import Showleague from './showleague';
import { Button } from 'bootstrap';

export let Context=createContext();
const handleclick=()=>{
    updateclick(!ButtonClicked)
}



function Preview({props}){
let ButtonClicked,updateclick=useState(false);

const players=props
console.log(props)


const wk=players.filter((item)=> 
item.role==="WK"
)  
const bat=players.filter((item)=> 
item.role==="BAT"
)  
const allrounders=players.filter((item)=> 
item.role==="AR"
)  
const bowl=players.filter((item)=> 
item.role==="BOWL"
)  
return(<div className='outerdiv'>
  <button onClick={()=>{
    updateclick(!ButtonClicked)
  }}>‚ùå</button>
   {/* <Context.Provider value={players}>
  <Team></Team>
      </Context.Provider> */}
   <div className='card'>
  {wk.map((item)=>{
   
   return(
    <div className='player-profile'>
              
    <img src={item.src} alt={item.src}/>
    <p className='card-playername'>{item.name}</p>
    </div>)
  }) }
  </div>
  <div className='card'>
  {bat.map((item)=>{
    return(  
             <div className='player-profile'>
              
             <img src={item.src} alt={item.src}/>
             <p className='card-playername'>{item.name}</p>
             </div>)
   })}
   </div>
   <div className='card'>
   {allrounders.map((item)=>{
    return(
      <div className='player-profile'>
              
             <img src={item.src} alt={item.src}/>
             <p className='card-playername'>{item.name}</p>
            </div>
           )
   })}</div>
   <div className='card'>
   {bowl.map((item)=>{
    return(
      
      <div className='player-profile'>
              
      <img src={item.src} alt={item.src}/>
      <p className='card-playername'>{item.name}</p>
      </div>)
   })

  }
  </div>

</div>
)
}
export default  Preview;
