import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const UserProfile = () => {
  const [profilePicture, setProfilePicture] = useState('');
  const [username, setUsername] = useState('');
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');

  const updateUserDetails = (newDetails) => {
    setProfilePicture(newDetails.profilePicture);
    setUsername(newDetails.username);
    setFullName(newDetails.fullName);
    setEmail(newDetails.email);
  };

  const handleUpdateClick = () => {
    const newDetails = {
      profilePicture: 'new-profile-picture.jpg',
      username: 'new_username',
      fullName: 'New User',
      email: 'newuser@example.com',
    };

    updateUserDetails(newDetails);
  };

  return (
    <div className="container mt-5">
      <div className="card position-fixed" style={{ top: '0', left: '0' }}>
        <img
          className="card-img-top"
          src={profilePicture}
          alt="Profile"
        />
        <div className="card-body">
          <p className="card-text">
            <strong>Username:</strong> {username}
          </p>
          <p className="card-text">
            <strong>Full Name:</strong> {fullName}
          </p>
          <p className="card-text">
            <strong>Email:</strong> {email}
          </p>
        </div>
      </div>
      <div className="ml-4">
        <h1>User Profile</h1>
        <button
          className="btn btn-primary mt-3"
          onClick={handleUpdateClick}
        >
          Update User Details
        </button>
      </div>
    </div>
  );
};

export default UserProfile;
