import React, { useState } from "react";
import { useLocation } from "react-router-dom";

import axios from "axios";
import { useEffect } from "react";
export default function Contests(){
    const[contests,setContests]=useState([])
    const location=useLocation();
    const queryparams=new URLSearchParams(location.search)
    const match_id=queryparams.get('match_id')
    useEffect(()=>{
        
    const fetchContests=async()=>{
        try{
        const response= await axios.get(`http://localhost:3001/contests/${match_id}`);
        setContests(response.data)
    }
    catch(error){
         console.error('error while fetching data',error)
    } }
    if(match_id){
        fetchContests();
    }
   
    
    },[match_id])
    return(<>
       {
        contests.map((contest)=>{
            return(<div className="card mb-5 bg-primary">
                <p>{contest.total_spots}</p>
                <label>{contest.contest_name}</label>
                <p>{contest.spots_filled}</p>
                <p>{contest.entryfee}</p>
                <p>{contest.winning_amount}</p>
            </div>)
        })
       }

    </>)
}
