import { useState } from "react"
import "./conteststyle.css"
//import Players from "./players";

  export default function Contest({user}){
    let[contest,updatecontest]=useState([4]);
    console.log(user)
    console.log(user.team)
  
    const spots=4;
    const entryfee=49;
    let [filled,updatespots]=useState(0);
    const handleClick=()=>{
      if(filled>=spots){
        alert("sorry league is full")
      }
      else{
      if(user.balance>=entryfee){
        user.balance=user.balance-entryfee;
        updatecontest(user);
        updatespots(++filled)
        alert("you have joined the contest successfully")
        user.team.map((item)=>{
          console.log(item.name)
          return null;
        })
    
  
      }
      else {
        console.log(user.balance)
        alert("you dont have enough money to join the contest")
      }
    }
    }
    let showspots=()=>{
      return(<ul><li>{contest.map((item)=>{
           <p>{item.name}</p>
      })}</li></ul>)
    }
return(<div className="contest"><div className="spots">
    <p className="size">Total Spots :{spots}</p>
    <p className="size">Teams joined:{filled}</p>
    </div>
    <div className="innerdiv" onClick={showspots}>
    
 <p className="join">join</p>  
  
<button onClick={handleClick}>{entryfee}</button>
</div>
</div>)
}
