import { useState } from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
//import Matches from "./Matches.js"
import "./playerstyle.css"
import {useNavigate} from "react-router-dom";
 //import Contest from "./contest.js"
//import Preview from "./previewpage";
//import { click } from "@testing-library/user-event/dist/click";
const initialcredits=100;
let initialplayers=0;
//let user={name:"Tharun",balance:500,team:[]};


 function Players({user}){
    const navigate=useNavigate();
    const goto=useNavigate();
    console.log(user)
    
    let[playerCount,updateplayercount]=useState(initialplayers);
  //  const[user,updateuser]=useState({name:"BluffMaster",balance:400,team:[]});

  //  const[playercount,updatecount]=useState(initialcount);
    const [credits,updatecredits]=useState(initialcredits);
    let players=[{name:"Ms Dhoni",credits:8,team:"CSK",src:"dhoni.jpg",role:"WK",Isselected:false,currentpoints:0},
    {name:"faf duplesis",credits:9,team:"RCB",src:"faf.jpg",role:"BAT",BroleIsselected:false,currentpoints:0},
    {name:"Virat Kohli",credits:9.5,team:"RCB",src:"virat.jpg",role:"BAT",Isselected:false,currentpoints:0},
     {name:"Glen Maxwell",credits:9.5,team:"RCB",src:"maxwell.jpg",role:"AR",Isselected:false,currentpoints:0},
     {name:"Ravindra Jadeja",credits:9.5,team:"CSK",src:"jadeja.jpg",role:"AR",Isselected:false,currentpoints:0},
     {name:"Dinesh Karthik",credits:7,team:"RCB",src:"dk.jpg",role:"WK",Isselected:false,currentpoints:0},
     {name:"Mohammad Siraj",credits:8.5,team:"RCB",src:"siraj.jpg",role:"BOWL",Isselected:false,currentpoints:0},
     {name:"Devon Conway",credits:8.5,team:"CSK",src:"conway.jpg",role:"WK",Isselected:false,currentpoints:0},
     {name:"Wanindu Hasaranga",credits:10,team:"RCB",src:"hasaranga.jpg",role:"AR",Isselected:false,currentpoints:0},
     {name:"Mooen Ali",credits:8.5,team:"CSK",src:"mooen.jpg",role:"AR",Isselected:false,currentpoints:0},
     {name:"Matheesha Pathirana",credits:8.5,team:"CSK",src:"pathirana.jpg",role:"BOWL",Isselected:false,currentpoints:0},
     {name:"Deepak Chahar",credits:8,team:"CSK",src:"deepak.jpg",role:"BOWL",Isselected:false,currentpoints:0},
     {name:"Shivam Dube",credits:8,team:"CSK",src:"dube.jpg",role:"AR",Isselected:false,currentpoints:0},
     {name:"David willey",credits:8.5,team:"RCB",src:"willey.jpg",role:"AR",Isselected:false,currentpoints:0},
     {name:"Ruturaj Gaikwad",credits:9,team:"CSK",src:"ruturaj.jpg",role:"BAT",Isselected:false,currentpoints:0},
     {name:"Kyle Jameison",credits:7.5,team:"CSK",src:"jamieson.jpg",role:"BOWL",Isselected:false,currentpoints:0},
     {name:"Ajinkhya Rahane",credits:9,team:"CSK",src:"rahane.jpg",role:"BAT",Isselected:false,currentpoints:0},
     {name:"Harshal Patel",credits:8.5,team:"RCB",src:"harshal.jpg",role:"BOWL",Isselected:false,currentpoints:0},
     {name:"Michael Bracewell",credits:9,team:"RCB",src:"bracewell.jpg",role:"AR",Isselected:false,currentpoints:0},
    ]
     ;
     

    const [player,updatelist]=useState(players)
   // console.log(player)


    const handleClick=(clickedplayer)=>{
        if(clickedplayer.credits<=credits||clickedplayer.Isselected){
        if(!clickedplayer.Isselected){
            if(playerCount==11){
                alert("cannot select more than 11 players")
                return null;
            }
            updateplayercount(++playerCount);
            console.log(playerCount)
        }
        else{

            updateplayercount(--playerCount);
            console.log(playerCount)
        }
    }
      
   const updatedplayerlist= player.map((item)=>{
  
        if(item.name===clickedplayer.name){
             if(item.credits>credits){
                if(item.Isselected===true){
                    
                    return {...clickedplayer,Isselected:!item.Isselected}
                }
                 alert("credits overflow")
             return item;
            }
    
       return  {...clickedplayer,Isselected:!item.Isselected}
        }
        
        return item;
     })
    
   const updatedcredits=()=>
   updatedplayerlist.reduce((totalcredits,item)=>{
    
    
    if(item.Isselected){
        return totalcredits-item.credits
    }
    return totalcredits;
    
   },initialcredits)
   console.log(updatedplayerlist)
 
 updatelist(updatedplayerlist)
   updatecredits(updatedcredits)
    }
    let updatedplayers=player.filter((item)=>{
        return (item.Isselected?item:null)
        });
    return(<>
    <div className="credits">
        <p className="left">Total credits:<span className="noraml">{100}</span></p>
        <p className="counterheading">Players Selected :<span className="counter"> {playerCount}</span></p>
        <p className="right">Remaining credits:<span className="danger">{credits}</span></p>
    </div>
    <ul>
    {player.map((item)=>{
        return(
        <li className={item.Isselected ? "selected" : ""} onClick={()=>handleClick(item)}>
            
            <img className="profile" src={process.env.PUBLIC_URL + '/'+item.src}/>
            <p className="name">{item.name}</p>    <p className="cr">{item.credits}</p> <p className="team">{item.team}</p><p className="role">{item.role}</p>
        </li>

        
        )
    })}</ul>
    <button className="nextbtn"  onClick={()=>{
        console.log(playerCount)
        if(playerCount===11){
         user.team=updatedplayers;
         navigate("/Contest")

        
        }
        else
        {

        alert("select 11 players")
        }
    }} >next</button> <button className="nxtbtn" onClick={()=>{
        console.log(updatedplayers)
           goto({path:'/Preview',
        state:{final11:updatedplayers}});
    }}>preview</button>
    </>)
}
export {Players}
