import { useState } from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
//import Matches from "./Matches.js"
import "./playerstyle.css"
import {useNavigate} from "react-router-dom";
import Contest from "./contest.js"



import "./playerstyle.css"
//import { click } from "@testing-library/user-event/dist/click";
const initialcredits=100;
let initialplayers=0;
let user={name:"Tharun",balance:500,team:[]};
export default function Players(user){
    const navigate=useNavigate();
    let[playerCount,updateplayercount]=useState(initialplayers);
  //  const[user,updateuser]=useState({name:"BluffMaster",balance:400,team:[]});

  //  const[playercount,updatecount]=useState(initialcount);
    const [credits,updatecredits]=useState(initialcredits);
    let players=[{name:"Ms Dhoni",credits:9,team:"CSK",src:"dhoni.jpg",role:"WK",Isselected:false},
    {name:"Rohit Sharma",credits:9.5,team:"MI",src:"rohit.jpg",role:"BAT",BroleIsselected:false},
    {name:"Virat Kohli+",credits:10,team:"RCB",src:"virat.jpg",role:"BAT",Isselected:false},
     {name:"Hardik Pandya",credits:9.5,team:"GT",src:"hardik.jpg",role:"AR",Isselected:false},
     {name:"Andre Russel",credits:10,team:"KKR",src:"russel.jpg",role:"AR",Isselected:false},
     {name:"KL Rahul",credits:9,team:"LSG",src:"klrahul.jpg",role:"WK",Isselected:false},
     {name:"Mohammad Shami",credits:9,team:"LSG",src:"shami.jpg",role:"BOWL",Isselected:false},
     {name:"David Warner",credits:10,team:"SRH",src:"Warner.jpg",role:"BAT",Isselected:false},
     {name:"Yuzi Chahal",credits:9.5,team:"RR",src:"chahal.jpg",role:"BOWL",Isselected:false},
     {name:"Aiden Markram",credits:8,team:"SRH",src:"markram.jpg",role:"AR",Isselected:false},
     {name:"Umran Malik",credits:8.5,team:"SRH",src:"umran.jpg",role:"BOWL",Isselected:false},
     {name:"Arshdeep Singh",credits:8,team:"PBKs",src:"arshdeep.jpg",role:"BOWL",Isselected:false}]
     ;

    const [player,updatelist]=useState(players)


    const handleClick=(clickedplayer)=>{
        if(clickedplayer.credits<=credits||clickedplayer.Isselected){
        if(!clickedplayer.Isselected){
            updateplayercount(++playerCount);
            console.log(playerCount)
        }
        else{

            updateplayercount(--playerCount);
            console.log(playerCount)
        }
    }
      
   const updatedplayerlist= player.map((item)=>{
  
        if(item.name===clickedplayer.name){
             if(item.credits>credits){
                if(item.Isselected===true){
                    
                    return {...clickedplayer,Isselected:!item.Isselected}
                }
                 alert("credits overflow")
             return item;
            }
    
       return  {...clickedplayer,Isselected:!item.Isselected}
        }
        
        return item;
     })
    
   const updatedcredits=()=>
   updatedplayerlist.reduce((totalcredits,item)=>{
    
    
    if(item.Isselected){
        return totalcredits-item.credits
    }
    return totalcredits;
    
   },initialcredits)
   updatelist(updatedplayerlist);
   updatecredits(updatedcredits)
    }
    return(<>
    <div className="credits">
        <p className="left">Total credits:<span className="noraml">{100}</span></p>
        <p className="counterheading">Players Selected :<span className="counter"> {playerCount}</span></p>
        <p className="right">Remaining credits:<span className="danger">{credits}</span></p>
    </div>
    <ul>
    {player.map((item)=>{
        return(
        <li className={item.Isselected ? "selected" : ""} onClick={()=>handleClick(item)}>
            
            <img src={process.env.PUBLIC_URL + '/'+item.src}/>
            <p className="name">{item.name}</p>    <p className="cr">{item.credits}</p> <p className="team">{item.team}</p><p className="role">{item.role}</p>
        </li>

        
        )
    })}</ul>
    <button className="nextbutton"  onClick={()=>{
        console.log(playerCount)
        if(playerCount===11){
         user.team=player;
         navigate("/Contest")

        
        }
        else
        {

        alert("select 11 players")
        }
    }} >next</button>
    </>)
}
export {user};
