            {showUserInfo && (
                <div className="user-info">
                    {user && (
                        <>
                            <h5>welcome to batball11 {user.username} </h5>
                            <UserProfile user={user}/>
                        </>
                    )}
                </div>
            )}

    <button className=" btn btn-primary" onClick={()=>{
        navigate("/user/matches")
    }}>start</button>
    </>)
    
}
